import React, { useState, useEffect } from "react";
import axios from "axios";
import { ResultTable } from "./components/Table"



function App() {
  const [employee, setEmployee] = useState({
    list: []
  })

  useEffect(() => {
    axios.get("https://randomuser.me/api/?results=20").then(res => {
      console.log(res)
      setEmployee({ list: res.data.results })
    })
  }, [])


  const filter = (e) => {
    console.log(e.target)
    setEmployee({ list: employee.list.sort((a, b) => a["name"].first.localeCompare(b["name"].first)) })
  }

  return (
    <div>
      <button onClick={(e) => filter(e)}>Click to Sort</button>
      <ResultTable results={employee.list} onCLick={filter} />
    </div>
  );
}

export default App;
